import { createSelector } from 'reselect'

const COLLECTION_ID_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
}

const selectCollections = state => state.shop.collections;

export const selectStoreCollections = createSelector(
    [selectCollections],
    (collections) => {
        console.log(`Collections selector: `, collections)
        return collections
    }
)

export const selectStoreCollection = collectionUrlParam =>
    createSelector(
        [selectCollections],
        collections =>
            collections.find(collection => collection.id === COLLECTION_ID_MAP[collectionUrlParam])
    )
