import { takeEvery } from 'redux-saga/effects'

import { ShopActionTypes } from './shop-types'

function* fetchCollectionsAsync() {

    // ? yeilds in callback give control back to saga library
    yield console.log(`Generator console`)

}

// ? Saga determines whether or not to cancel sagas

export function* fetchCollectionStart() {

    // ? Takeevery creates a nonblocking code so app still other scripts or sagas are not blocked
    yield takeEvery(
        ShopActionTypes.FETCH_COLLECTIONS_START, // action this saga listens for
        fetchCollectionsAsync // what is excecuted and controled by saga
    )

}
