import React from 'react';
import axios from 'axios'
import StripeCheckout from 'react-stripe-checkout'

const StripeButton = ({ price }) => {

    const priceForStripe = price * 100;
    const publishableKey = 'pk_test_swTY0zxorbkFik3g0gjh5Qq400wSMrNfsJ'

    // When Payment is made token is returned
    const onToken = (token) => {
        console.log(token)

        axios({

            // ? access will infer current url and append payment to it
            url: 'payment',
            method: 'post',
            data: {
                amount: priceForStripe,
                token
            }
        })
            .then((res) => {
                alert('Payment was successful!')
            })
            .catch((err) => {
                console.log('Error in stripe payment: ', JSON.parse(err))
                alert('There was an error with the payment, please use the provided card. Thank you! ')
            })

    }


    return (
        <StripeCheckout
            label='Pay Now'
            name='DMN Clothing'
            billingAddress
            shippingAddress
            image='https://svgshare.com/i/LGM.svg'
            description={`Your total is $${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            stripeKey={publishableKey}
        />

    );


};

export default StripeButton;
