// ? Selectors for getting slices of state
// ? CreateSelector makes memoized selectors
import { createSelector } from 'reselect'

// ? Input selector getting the state of cart 
// ? State passed tp selector will reference here
const selectCart = state => state.cartDropDown;
const selectCartItemsList = state => state.cartDropDown.cartItems;

export const selectCartItems = createSelector(
    [selectCartItemsList], //? Input selector being referenced
    // ? Recieves input selector output as argument
    (cartDropDown) => {
        console.log(`Select cart selector:`,cartDropDown)
        return cartDropDown
    }
)

export const selectCartItemsCount = createSelector(
    [selectCart],
    (selectCart) => {
        console.log(`Cart items count selcetor:`)
        return selectCart.cartItems.reduce((accumulator, currentValue) => {
            return accumulator + currentValue.quantity
        }, 0)
    }
)

export const selectCartVisibility = createSelector(
    [selectCart],
    (cartDropDown) => {
        console.log(`Cart Visibility selcetor:`,cartDropDown.hideCart)
        return cartDropDown.hideCart
    }
)
