import React from 'react';
import { useSelector, shallowEqual } from 'react-redux'
import { createStructuredSelector } from 'reselect'

import { selectCartItems } from './../../redux/cart-dropdown/selectors/cart'

import './checkout.scss'

const Checkout = () => {

    const cartItems = useSelector(selectCartItems)


    // ? Structured selector makes it easier to add and use multiple selectors
    const structuredSelector = createStructuredSelector({
        cartItems: (state) => selectCartItems(state),
        cartDropdownHidden: (state) => selectCartVisibility(state)
    })

    const { currentUser, cartDropdownHidden } = useSelector(structuredSelector, shallowEqual);

    return (
        <div className="checkout-page">

            <div className="checkout-header">

                <div className="header-block">

                    <span>Product</span>

                </div>

                <div className="header-block">

                    <span>Description</span>

                </div>

                <div className="header-block">

                    <span>Price</span>

                </div>

                <div className="header-block">

                    <span>Remove</span>

                </div>

            </div>

        </div>
    );

};

export default Checkout;
