import React from 'react';
import lodash from 'lodash'

import { connect, useSelector, useDispatch } from 'react-redux'
import { selectCartItems } from '../../../redux/cart-dropdown/selectors/cart-items'
import { toggleCartDisplay } from '../../../redux/cart-dropdown/cart-dropdown-actions'

import { ReactComponent as ShoppingCartIcon } from "../../../assets/shoppingbag.svg";

import './cart-icon.scss'

const CartIcon = () => {

    const totalItems = useSelector((state) => selectCartItems(state), lodash.isEqual)

    const dispatch = useDispatch()

    console.log("SnackList rendering");

    return (

        <div
            className="cart-icon"
            // Changes state being used somewheree else so does not cause rerender of cart icon
            onClick={() => dispatch(toggleCartDisplay())}
        >

            <ShoppingCartIcon className="shopping-icon" />
            <span className="item-count">{totalItems}</span>

        </div>
    );

};

