// ? Selectors for getting slices of state
// ? CreateSelector makes memoized selectors
import { createSelector } from 'reselect'

// ? Input selector getting the state of cart 
// ? State passed tp selector will reference here
const selectCart = state => state.cartDropDown;
const selectVisibility = state => state.cartDropDown.hideCart;
const selectList = state => state.cartDropDown.cartItems;

export const selectCartItems = createSelector(
    [selectCart], //? Input selector being referenced
    // ? Recieves input selector output as argument
    (cartDropDown) => {
        console.log(`Select cart selector:`, cartDropDown.cartItems)
        return cartDropDown.cartItems
    }
)

export const selectCartItemsCount = createSelector(
    [selectList],
    (selectCartList) => {
        const total = selectCartList.reduce((accumulator, currentValue) => {
            return accumulator + currentValue.quantity
        }, 0)
        console.log(`Cart items count selcetor:`, total)
        return selectCartList.reduce((accumulator, currentValue) => {
            return accumulator + currentValue.quantity
        }, 0)
    }
)

export const selectCartVisibility = createSelector(
    [selectVisibility],
    (selectCartVisibility) => {
        console.log(`Cart Visibility selcetor:`, selectCartVisibility)
        return selectCartVisibility
    }
)
