import React from 'react';
import './forminput.scss';

// ? Destrcuct values and object from props object
// const FormInput = ({ handleChange, label, ...otherProps }) => {
const FormInput = (props) => {

    console.log(...props)

    return (
        <div className={`group `}>
            {/* {console.log(...props)} */}
            {/* https://www.carlrippon.com/writing-concise-react-components-with-destructure-assignment-and-spread/
                Passing props with spread operator trick (Check your notes) 
                EX// {name:email} -> {...otherProps} -> name
            */}
            <input className='form-input' onChange={props.handleChange} />

            {
                props.label ? (
                    <label
                        className={`${
                            props.value.length ? 'shrink' : ''
                            } form-input-label`}
                    >
                        {props.label}

                    </label>

                ) : null
            }

        </div>
    );
};

export default FormInput;
