// ? Selectors for getting slices of state
// ? CreateSelector makes memoized selectors
import { createSelector } from 'reselect'

// ? Input selector getting the state of cart 
const selectCart = state => state.cartDropDown;

// ?
export const selectCartItems = createSelector(
    [selectCart], //? Input selector
    // ? Recieves input selector out put as argument
    (cartDropDown) => cartDropDown.cartItems
)

export const getTotalItems = (state) => {
    return state.cartDropDown.cartItems.reduce((accumulator, currentValue) => {
        return accumulator + currentValue.quantity
    }, 0)
}
