import React from 'react';
import { useSelector } from 'react-redux'
import { createStructuredSelector } from 'reselect'

import { selectIsFetchingCollections } from '../../redux/shop/shop-selectors'

import CollectionOverview from './collections-overview'
import WithSpinner from '../with-spinner/with-spinner'


const CollectionsOverviewContainer = (props) => {

    const structuredSelector = createStructuredSelector({
        isLoading: (state) => selectIsFetchingCollections(state)
    })

    const { isLoading } = useSelector(structuredSelector);
    console.log(`!!!!!!!`, isLoading)

    const clonedCollectionComponent = React.cloneElement(CollectionOverview,
    {isLoading: isLoading})

    return (
        WithSpinner(clonedCollectionComponent)
    )

};

export default CollectionsOverviewContainer;

