import { createSelector } from 'reselect'

const selectShop = state => state.shop;

export const selectShopCollections = createSelector(
    [selectShop],
    (shop) => {
        console.log(`shop collections selector: `, shop.collections)
        return shop.collections
    }
)

export const selectCollectionsForPreview = createSelector(
    [selectShop],
    (shopCollections) => {
        console.log(`shopCollections collections into an array selector: `, shopCollections.collections);
        Object.keys(shopCollections).map(key => shopCollections[key])
        return shopCollections.collections
    }
)

export const selectStoreCollection = (collectionUrlParam) =>
    createSelector(
        [selectShopCollections],
        shopCollection => {
            console.log(`shop specific collection selector: `, shopCollection[collectionUrlParam])
            return shopCollection[collectionUrlParam]
        }

    )
