import React from 'react';

import { Link } from "react-router-dom";
import { auth } from '../config/firebase/firebase-util'
import { useSelector } from 'react-redux'

// ? Necesssary naming to import svg file
import { ReactComponent as Logo } from '../../assets/logo.svg'
import CartIcon from '../cart/cart-icon/cart-icon'
import CartDropdown from '../cart/cart-dropdown/cart-dropdown';

import lodash from 'lodash'

import './header-styles.scss'

const Header = () => {

    const currentUser = useSelector((state) => {
        return state.user.currentUser
    }, lodash.isEqual)

    const cartDropdownHidden = useSelector((state) => {
        return state.cartDropDown.hideCart
    })

    const signOut = async () => {
        await auth.signOut();
    }

    return (
        <div className="header">

            <Link
                className="logo-container"
                to="/"
            >
                <Logo className="logo" />

            </Link>

            <div className="nav-links">

                <Link className="link" to="/shop">
                    SHOP
                </Link>

                <Link className="link" to="/">
                    Contact
                </Link>

                {

                    currentUser ?
                        <div
                            className="link"
                            onClick={signOut}
                        >
                            SIGN OUT
                        </div>
                        :
                        <Link className="link" to="/signin">
                            SIGN IN
                        </Link>

                }

                <CartIcon />

            </div>

            {
                cartDropdownHidden ?
                    null :
                    <CartDropdown />
            }


        </div>
    );
};

export default React.memo(Header);
