const initialState = {
    currentUser: null
}

const useReducer = (state = initialState, action) => {
    switch (action.type) {
        case ('SET_CURRENTUSER'):
            return {
                ...state,
                currentUser: action.payload
            }

        default:
            return state;

    }
}

export default useReducer;
