import { ShopActionTypes } from './shop-types'
import { firestore, convertCollectionSnapShotToMap } from '../../components/config/firebase/firebase-util'

// ? Switches Reducer fetching state
export const fetchCollectionsStart = () => {
    return {
        type: ShopActionTypes.FETCH_COLLECTIONS_START,
    }
}


export const fetchCollectionsSuccess = (collectionMap) => ({

    type: ShopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload: collectionMap

})


export const fetchCollectionFailure = (errorMessage) => ({

    type: ShopActionTypes.FETCH_COLLECTIONS_FAILURE,
    payload: errorMessage

})

// ? With thunk middleware enabled, any dispatch of a function instead of an object will be given dispatch 
// ? as it's' first argument
export const fetchCollectionsStartAsync = () => {

    // ? Thunks are just functions that return a function with access to dispatch
    return dispatch => {

        const collectionRef = firestore.collection('collections')
        dispatch(fetchCollectionsStart())

        collectionRef.get()
            .then(snapshot => {
                const collectionsMap = convertCollectionSnapShotToMap(snapshot)
            })
            .catch(error => dispatch(fetchCollectionFailure())) 

    }
}
