import styled from 'styled-components'

export const CartDropDownContainer = styled.div` 
position: absolute;
width: 240px;
height: 340px;
display: flex;
flex-direction: column;
padding: 10px;
border: 1px solid black;
background-color: white;
top: 90px;
right: 40px;
z-index: 5;

.empty-message {
  font-size: 1.4rem;
  margin: 2.8rem auto;
}

.cart-items {
  height: 240px;
  display: flex;
  flex-direction: column;
  overflow: scroll;
}

button {
  margin-top: auto;
  background-color:green;
}
`
