import { takeEvery } from 'redux-saga'

import ShopActionTypes from './shop-types'

function* fetcCollectionsAsync() {

    // ? yeilds in callback give control back to saga library
    yield console.log(`Generator console`)

}


export function* fetchCollectionStart() {

    // ? Take every creates a nonblocking code so app still other scripts or sagas are not blocked
    yield takeEvery(
        ShopActionTypes.FETCH_COLLECTIONS_START,
        fetcCollectionsAsync
     )

}
