import cartActionTypes from "./cart-dropdown-types";

const initialState = {
    hideCart: true,
    cartItems: []
}

export default (state = initialState, { type, payload }) => {
    switch (type) {

        case cartActionTypes.TOGGE_CART_DISPLAY:
            return {
                ...state,
                hideCart: !state.hideCart
            }

        default:
            return state
    }
}
