import React, { useState } from 'react';
import FormInput from '../shared/forminput/FormInput'
import { auth, createUserProfileDocument } from '../config/firebase/firebase-util'

import './dynamic.scss'
import '../shared/button/google-button.scss'

const Login = () => {

    const [userInput, setuserInput] = useState({ name: '', email: '', password: '', confirmPassword: '' })


    const handleSignUp = async (event) => {

        event.preventDefault();

        const { name, email, password, confirmPassword } = userInput

        if (password !== confirmPassword) {
            alert('Passwords do not match')
            return;
        }

        try {

            // ? Creates user and signs user in, returns object with user key to get user data 
            const { user } = await auth.createUserWithEmailAndPassword(email, password)


            // ? Save the created user into firebaseDB
            createUserProfileDocument(user, { name })

            setuserInput(
                {
                    name: '',
                    email: '',
                    password: '',
                    confirmPassword: ''
                }
            )

        } catch (error) {

        }

    }

    const handleUserInput = (e) => {

        // ? Name attribute in input allows getting the proper input
        console.log(`INPUT NAME: `, e.target.name)

        // ? Copy UserInput, Then change one property
        // ? (Will modify not add if new property would have been in spread)
        setuserInput(
            {
                ...userInput,
                [e.target.name]: e.target.value,
            }
        )

    }

    return (

        <form
            className="form sign-up"
            onSubmit={handleSignUp}
        >

            <h2>Time to feel like home</h2>

            <FormInput
                name='name'
                type='text'
                handleChange={handleUserInput}
                value={userInput.email}
                label='Name'
                required
            />

            <FormInput
                name='email'
                type='email'
                handleChange={handleUserInput}
                value={userInput.email}
                label='Email'
                required
            />

            <FormInput
                name='password'
                type='password'
                handleChange={handleUserInput}
                value={userInput.password}
                label='Password'
                required
            />

            <button type="button" className="submit" onClick={handleSignUp}>Sign Up</button>
            <button onClick={() => { let a; }} type="button" className="loginBtn loginBtn--google">Join with <span>Google</span></button>

            {/* <input type="submit" value="Submit Form" /> */}

        </form>

    );

};

export default Login;
