import React, { useState } from 'react';

const Login = () => {

    const [userInput, setuserInput] = useState({ email: '', password: '' })


    const handleSignIn = (event) => {

        event.preventDefault();
        setuserInput({ email: '', password: '' })
        // console.log(userInput.email)

    }

    const handleUserInput = (e) => {

        // ? Name attribute in input allows getting the proper input
        // console.log(`INPUT NAME: `, e.target.name)

        // ? Copy UserInput, Then change one property
        // ? (Will modify not add if new property would have been in spread)
        setuserInput(
            {
                ...userInput,
                [e.target.name]: e.target.value,
            }
        )

    }

    return (

        <div /*className='sign-in' */>

            <div className="form sign-in">

                <h2>Welcome back,</h2>

                <label>
                    <span>Email</span>
                    <input type="email" />
                </label>

                <label>
                    <span>Password</span>
                    <input type="password" />
                </label>

                <p className="forgot-pass">Forgot password?</p>
                <button type="button" className="submit">Sign In</button>
                <button type="button" className="fb-btn">Connect with <span>facebook</span></button>

            </div>

            {/* ******************* */}


            <form
                className="form sign-in"
                onSubmit={handleSignIn}
            >

                <h2>Welcome back</h2>
                {/* <span>Sign in with your email and password</span> */}


                <label>

                    <span>Email</span>

                    <input
                        name="email"
                        type="email"
                        onChange={handleUserInput}
                        value={userInput.email}

                    />
                </label>


                <label>

                    <span>Password</span>

                    <input
                        name="password"
                        type="password"
                        onChange={handleUserInput}
                        value={userInput.password}
                    />

                </label>


                <label> Password </label>

                <p className="forgot-pass">Forgot password?</p>
                <button type="button" className="submit">Sign In</button>
                <button type="button" className="fb-btn">Connect with <span>facebook</span></button>

                <input type="submit" value="Submit Form" />

            </form>

            {/* ******************* */}
            {/* <h2>Already a member?</h2>
            <span>Sign in with your email and password</span>

            <form onSubmit={handleSignIn}>

                <input
                    name="email"
                    type="email"
                    onChange={handleUserInput}
                    value={userInput.email}

                />

                <label> Email </label>

                <input
                    name="password"
                    type="password"
                    onChange={handleUserInput}
                    value={userInput.password}
                />

                <label> Password </label>

                <input type="submit" value="Submit Form" />

            </form> */}

        </div>
    );

};

export default Login;
